import React, { Component } from 'react';
import FileBase64 from 'react-file-base64';
import { Base64 } from 'js-base64';
import { Column, Box, Button } from 'bloomer';

class UploadFile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      file: ''
    }
    this.getFile = this.getFile.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit() {
    this.setState({
      file: ''
    });
  }

  getFile(file) {
    // let temp = file.base64;
    // temp = Base64.decode();

    let temp = document.getElementById("xml").InnerText;


    console.log(temp);
    this.setState({file: temp});

    this.props.handler({xmlFile: this.state.file});

  }

  render() {
    return (
      <Box>  
        <div className="field"> 
            <div className="control">
              <textarea className="textarea is-info" id="xml" placeholder="XML" rows="10"></textarea>
            </div>
        </div>
        <p>or</p>
        <Column>
          <FileBase64 multiple={false} onDone={this.getFile} />
        </Column>
        <div className="menu-row" >
          <Button isColor='info' onClick={this.getFile}>Convert</Button>
        </div>
      </Box>
    )
  }

}

export default UploadFile;
