import React, { Component } from 'react';
import { Column, Columns, Container, Notification } from 'bloomer';
import Xml2Json from '../components/Xml2Json';
class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            xmlFile: ""
        }
        this.handler = this.handler.bind(this);
    }
    handler(state) {
        this.setState(state);
    }

    render() {
        return (
            <Container isFluid>
                <Container style={{marginTop: 10, fontSize: 30, marginBottom: 10}}>
                    <Notification>XML to Json</Notification>
                </Container>
                <Container>
                    <Columns>
                        <Column>
                                <Xml2Json handler={this.handler} xmlFile={this.state.xmlFile}/>

                        </Column>
                    </Columns>
                </Container>
                <Footer id='footer'>
                <Container>
                    <Content>
                        <Columns>
                            <Column isFull>
                                <p>
                                    Made with<Icon hasTextColor="danger" className="fa fa-heart"></Icon> 
                                    by <a>Algus Dark</a>
                                </p>
                            </Column>
                        </Columns>
                        <Content isSize='small'>
                            <p>The source code is licensed under <a target="_blank">MIT</a>.</p>
                            <p>The website content is licensed under <a target="_blank">CC ANS 4.0</a>.</p>
                        </Content>
                    </Content>
                </Container>
            </Footer>
            </Container>
        );
    }
}

export default Home;
