import React, { Component } from 'react';
import { Column, Columns, Container, Title, Box, Tag, Button, Notification } from 'bloomer';
import Json2xml from '../components/Json2xml';
import UploadFile from '../components/UploadFile';
class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            xmlFile: ""
        }
        this.handler = this.handler.bind(this);
        this.showResult = this.showResult.bind(this);
    }
    handler(state) {
        this.setState(state);
    }

    showResult(){
        if(this.state.xmlFile != ""){
            document.getElementById("json").value = this.state.xmlFile;
        }
    }




    render() {
        this.showResult();
        return (
            <Container isFluid>
                <Container style={{marginTop: 10, fontSize: 30}}>
                    <Notification>XML to Json</Notification>
                </Container>
                <Container>
                    <Columns>
                        <Column>
                                <UploadFile handler={this.handler} xmlFile={this.state.xmlFile}/>

                        </Column> 
                        </Columns>
                        <Columns> 
                        <Column>
                                <Json2xml/>
                        </Column>
                        </Columns>
                        <Columns> 
                        <Column>
                            <Box>
                                <div className="field">
                                    <div className="control">
                                        <textarea className="textarea is-info" id="json" value placeholder="Json" rows="10"></textarea>
                                    </div>
                                </div>
                            </Box>
                        </Column>
                    </Columns>
                </Container>
            </Container>
        );
    }
}

export default Home;
