import React, { Component } from 'react';
import { Column, Columns, Container, Title, Box, Tag, Button, Notification } from 'bloomer';
import Json2xml from '../components/Json2xml';
import UploadFile from '../components/UploadFile';
class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
        }
        this.handler = this.handler.bind(this);
    }
    handler(state) {
        this.setState(state);
    }


    render() {
        return (
            <Container isFluid>
                <Container style={{marginTop: 10, fontSize: 30}}>
                    <Notification>XML to Json</Notification>
                </Container>
                <Container>
                    <Columns>
                        <Column is>
                            <Box>
                                <div class="field">
                                    <div class="control">
                                        <textarea class="textarea is-info" placeholder="XML" rows="10"></textarea>
                                    </div>
                                </div>
                                <p>or</p>
                                <UploadFile/>
                            </Box>
                        </Column>  
                        <Column>
                                <Json2xml/>
                        </Column> 
                        <Column>
                            <Box>
                                <div class="field">
                                    <div class="control">
                                        <textarea class="textarea is-info" placeholder="Json" rows="10"></textarea>
                                    </div>
                                </div>
                                <p>or</p>
                                <UploadFile/>
                            </Box>
                        </Column>
                    </Columns>
                </Container>
            </Container>
        );
    }
}

export default Home;
