import React, { Component } from 'react';
import { Column, Columns, Container, Title, Box, Tag, Button, Notification } from 'bloomer';
import Json2xml from '../components/Json2xml';
import UploadFile from '../components/Xml2Json';
import Xml2Json from '../components/Xml2Json';
class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            xmlFile: ""
        }
        this.handler = this.handler.bind(this);
        this.showResult = this.showResult.bind(this);
    }
    handler(state) {
        this.setState(state);
    }

    showResult(){
        if(this.state.xmlFile != ""){
            document.getElementById("json").value = this.state.xmlFile;
        }
    }

    render() {
        this.showResult();
        return (
            <Container isFluid>
                <Container style={{marginTop: 10, fontSize: 30}}>
                    <Notification>XML to Json</Notification>
                </Container>
                <Container>
                    <Columns>
                        <Column>
                                <Xml2Json handler={this.handler} xmlFile={this.state.xmlFile}/>

                        </Column>
                    </Columns>
                </Container>
            </Container>
        );
    }
}

export default Home;
